var regUpperCase = /([A-Z])/g,
    regSeparator = /[_.\- ]+/g,
    regTrim = /(^-)|(-$)/g;

exports = function exports(str) {
    str = str
        .replace(regUpperCase, '-$1')
        .toLowerCase()
        .replace(regSeparator, '-')
        .replace(regTrim, '');
    return str.split('-');
};

module.exports = exports;
